import React, { Component } from 'react'

 class EventBind extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
            message:'Hello'
      }
      this.clickHandler=this.clickHandler.bind(this)
    }
    
 clickHandler() 
    {
    this.setState({
        message:'good bye!!!'
    })   
    }
  render() {
    return (
      <div>
        <div>{this.state.message}</div>
        {/*First approach to bind event with bind*/}
        {/* <button onClick={this.clickHandler.bind(this)}>Click</button> */}
        {/*2nd approach to bind event with arrow function*/}
        {/* <button onClick={()=>this.clickHandler()} >Click </button>  */}
        {/*3rd approach to bind event with Constructor ---React official si*/}
        {/* <button onClick={this.clickHandler} >Click </button>  */}
        {/*4th approach to bind event*/}

      </div>
    )
  }
}

export default EventBind
